INSERT INTO `productos` (idProductos, `nombre`, `edadMinima`, `productimage_id`, `Descripcion`, `FechaSalida`, `precioSalida`, `publisher_idPublisher`, `genre_idGenre`, `region_idRegion`) VALUES (1, 'Daying light 2: Stay Human', 18, 1, 'Dying Light 2 es un videojuego de mundo abierto en primera persona que se sitúa en un mundo postapocalíptico en el que se ha sido víctima de una epidemia zombi. Al igual que la primera entrega, el jugador cuenta con varias habilidades del parkour, pudiendo escalar salientes, deslizarse, saltar desde cornisas y correr por la pared para poder desplazarse más rápidamente.', '2022-02-04 20:32:37', 59.99, 2, 1, 1);
INSERT INTO `productos` (idProductos, `nombre`, `edadMinima`, `productimage_id`, `Descripcion`, `FechaSalida`, `precioSalida`, `publisher_idPublisher`, `genre_idGenre`, `region_idRegion`) VALUES (2, 'Dying light ', 18, 2, 'Dying Light es un juego con un amplio y peligroso mundo abierto ubicado en la ciudad ficticia de Harran. Durante el día los jugadores podrán explorar un amplio ambiente urbano y expansivo contaminado por un insidioso virus, también podrán colocar las trampas y a su vez podrán buscar suministros en el día tanto como en la noche, salvar a los supervivientes encontrados.', '2015-01-27 20:32:37', 59.99, 3, 1, 1);
INSERT INTO `productos` (idProductos, `nombre`, `edadMinima`, `productimage_id`, `Descripcion`, `FechaSalida`, `precioSalida`, `publisher_idPublisher`, `genre_idGenre`, `region_idRegion`) VALUES (3, 'New World', 16, 3, 'New World es un videojuego de rol multijugador masivo en línea desarrollado por Amazon Game Studios. Ambientado a mediados del siglo XVII, los jugadores tendrán la tarea de colonizar tierras modeladas a imagen y semejanza de la América Británica oriental', '2021-09-28 20:39:56', 59.99, 4, 6, 1);
INSERT INTO `productos` (idProductos, `nombre`, `edadMinima`, `productimage_id`, `Descripcion`, `FechaSalida`, `precioSalida`, `publisher_idPublisher`, `genre_idGenre`, `region_idRegion`) VALUES(4, 'Call of Duty Black Ops 3', 16,  10,'Call of Duty: Black Ops III combina tres modos de juego únicos: campaña, multijugador y Zombis para proporcionar a los fans la experiencia Call of Duty más profunda y ambiciosa hasta la fecha.', '2015-11-06 20:39:56', 59.99, 3, 5, 2);
INSERT INTO `productos` (idProductos, `nombre`, `edadMinima`, `productimage_id`, `Descripcion`, `FechaSalida`, `precioSalida`, `publisher_idPublisher`, `genre_idGenre`, `region_idRegion`) VALUES(5, 'Grand Theft Auto 5', 18,  9,'Grand Theft Auto V para PC ofrece a los jugadores la opción de explorar el galardonado mundo de Los Santos y el condado de Blaine con una resolución de 4K y disfrutar del juego a 60 fotogramas por segundo.supervivientes encontrados.', '2013-11-20 20:32:37', 59.99, 3, 6, 6);
INSERT INTO `productos` (idProductos, `nombre`, `edadMinima`, `productimage_id`, `Descripcion`, `FechaSalida`, `precioSalida`, `publisher_idPublisher`, `genre_idGenre`, `region_idRegion`) VALUES(6, 'Cities: Skylines', 6,  5,'Cities: Skylines presenta un enfoque moderno para el clásico simulador urbano. Introduce nuevos elementos a la mecánica de juego para plasmar la emoción y las complicaciones de mantener una ciudad de verdad, ampliando al mismo tiempo algunos de los temas ya conocidos de la experiencia del desarrollo urbanístico.desplazarse más rápidamente.', '2015-03-15 00:00:37', 50, 2, 2, 1);
INSERT INTO `productos` (idProductos, `nombre`, `edadMinima`, `productimage_id`, `Descripcion`, `FechaSalida`, `precioSalida`, `publisher_idPublisher`, `genre_idGenre`, `region_idRegion`) VALUES(7, 'Stardew Valley', 12,  8,'Te vas a vivir al valle... Acabas de heredar la vieja parcela agrícola de tu abuelo de Stardew Valley. Decides partir hacia una nueva vida con unas herramientas usadas y algunas monedas. ¿Te ves capaz de vivir de la tierra y convertir estos campos descuidados en un hogar próspero?desplazarse más rápidamente.', '2016-02-26 20:32:37', 59.99, 1, 6, 5);
INSERT INTO `productos` (idProductos, `nombre`, `edadMinima`, `productimage_id`, `Descripcion`, `FechaSalida`, `precioSalida`, `publisher_idPublisher`, `genre_idGenre`, `region_idRegion`) VALUES(8, 'Human fall Flat', 6,  7, 'Human: Fall Flat es un divertido juego de plataformas ambientado en paisajes flotantes y oníricos para hasta 8 jugadores en línea. Ahora con niveles gratuitos para recompensar a una comunidad muy entregada.supervivientes encontrados.', '2016-07-22 20:32:37', 59.99, 1, 6, 2);
